script:
frappe.ui.form.on('Car Booking', {
    refresh: function (frm) {
        // Add a trigger on the model field change event
        frm.fields_dict['model'].get_query = function (doc, cdt, cdn) {
            return {
                filters: {
                    // none
                }
            };
        };

        // Add a trigger on the model_type field change event
        frm.fields_dict['model_type'].get_query = function (doc, cdt, cdn) {
            // Get the selected model value
            var model = locals[cdt][cdn].model;

            // Return car model
            return {
                filters: {
                    'car_model': model
                }
            };
        };

        // Add a trigger on the color field change event
        frm.fields_dict['color'].get_query = function (doc, cdt, cdn) {
            // Get the selected model value
            var model = locals[cdt][cdn].model;

            // Return car model color
            return {
                filters: {
                    'car_model': model
                }
            };
        };
    }
});
